//setupdata function

function setupCanvas(barCharData) {
    const svg_width = 1100;
    const svg_height = 1200;
    const chart_margin = { top: 250, right: 40, bottom: 40, left: 100 };
    const chart_width = svg_width - (chart_margin.left + chart_margin.right);
    const chart_height = svg_height - (chart_margin.top + chart_margin.bottom);

    const this_svg = d3.select('.bar-chart-container').append('svg')
        .attr('width', svg_width).attr('height', svg_height).append('g').attr('transform', `translate(${chart_margin.left},${chart_margin.top})`);

    //scale 
    //domain 放資料， range 放圖
    const xExtent = d3.extent(barCharData, d => d[1]);
    const xMax = d3.max(barCharData, d => d[1]);
    const xScale_v3 = d3.scaleLinear([0, xMax], [0, chart_width]);
    const yScale = d3.scaleBand().domain(barCharData.map(d => d[0])).rangeRound([0, chart_height]).paddingInner(0.25);
    //Draw bars
    const bars = this_svg.selectAll('.bar')
        .data(barCharData)
        .enter()
        .append('rect')
        .attr('class', 'bar')
        .attr('x', 0)
        .attr('y', d => yScale(d[0]))
        .attr('width', d => xScale_v3(d[1]))
        .attr('height', yScale.bandwidth())
        .style('fill', '#B3B379')

    //Draw header

    const header = this_svg.append('g').attr('class', 'bar-header')
        .attr('transform', `translate(0,${-chart_margin.top / 2})`)//decide the position of header
        .append('text');

    header.append('tspan').text("你所適合的旅遊類別一覽表").style('font-size', '2.5em');
    header.append('tspan').text("最高一百，最低零分")
        .attr('x', 0).attr('y', 50).style('font-size', '2em').style('fill', '#555');//調整位置


    var arrfavoriteTravel = [];
    var forText="";
    for (var i = 0; i < 6; i++) {
        if (barCharData[i][1] == barCharData[0][1]) {
            arrfavoriteTravel.push(barCharData[i][0]);
            forText+=barCharData[i][0]+" ";
        }
    }
    header.append('tspan').text("最適合你的旅遊類型為: " +forText).attr('x', 0).attr('y', 100).style('font-size', '1.5em');
    // tail.append('tspan').text("最高一百，最低零分")
    //.attr('x', 0).attr('y', 50).style('font-size', '2em').style('fill', '#555');//調整位置
    // let travel = document.getElementById('travel-place-container')
    // travel.innerHTML += "最適合你的旅遊類型為: ";

    var attractionWebsite = [];
    var attractionType = [];
    var titleAttraction = [];
    dataExecution(attractionType, attractionWebsite, titleAttraction);
    randomSelectPlace(arrfavoriteTravel, attractionType, attractionWebsite, titleAttraction);
    //tickSizeInner : the length of the tick lines
    //tickSizeOuter : the length of the square ends of the domain path

    const xAxis = d3.axisTop(xScale_v3).tickSizeInner(-chart_height)
        .tickSizeOuter(0);

    const xAxisDraw = this_svg.append('g').attr('class', 'x axis').call(xAxis);

    const yAxis = d3.axisLeft(yScale).tickSize(0);
    const yAxisDraw = this_svg.append('g')
        .attr('class', 'y axis')
        .call(yAxis);

    yAxisDraw.selectAll('text').attr('dx', '-0.6em').style('font-size', '2em');
}
