function randomSelectPlace(arrfavoriteTravel, attractionType, attractionWebsite, titleAttraction) {

    //console.log(attractionWebsite);
    //console.log(attractionType);
    var selectfavoriteTypeAttraction = [];
    var selectfavoriteNameAttraction = [];
    for (var i = 0; i < attractionType.length; i++) {
        for (var j = 0; j < attractionType[i].length; j++) {
            for (var k = 0; k < arrfavoriteTravel.length; k++) {
                if (arrfavoriteTravel[k] == attractionType[i][j] && !(selectfavoriteTypeAttraction.includes(attractionWebsite))) {

                    selectfavoriteTypeAttraction.push(attractionWebsite[i]);
                    selectfavoriteNameAttraction.push(titleAttraction[i]);
                }
            }
        }
    }
    console.log(selectfavoriteTypeAttraction);
    //選出符合類型的景點
    //亂數選出三個景點
    var randomNumber = [-1, -1, -1];

    do {
        randomNumber[0] = Math.floor(Math.random() * selectfavoriteTypeAttraction.length);
    } while (randomNumber[0] === randomNumber[1])

    do {
        randomNumber[1] = Math.floor(Math.random() * selectfavoriteTypeAttraction.length)
    } while (randomNumber[1] === randomNumber[2] || randomNumber[1] === randomNumber[0])

    do {
        randomNumber[2] = Math.floor(Math.random() * selectfavoriteTypeAttraction.length)
    } while (randomNumber[2] === -1 || randomNumber[2] === randomNumber[1] || randomNumber[2] === randomNumber[0])
    console.log(randomNumber);

    //在顯示適合旅遊的地方加button，按下之後顯示三個random出來的行程網頁

    $('#bar-chart-container').append("<button type='button' class='myTravel' id ='myTravel' ><span>推薦行程</span></button>");
    $(function () {
        $('#myTravel').on('click', function () {
            if ($('#Travel-Place-Final').is(':empty')) {
                for (var i = 0; i < 3; i++) {
                    $('#Travel-Place-Final').append(`<li><a href=${selectfavoriteTypeAttraction[randomNumber[i]]}>${selectfavoriteNameAttraction[randomNumber[i]]}</a></li>`);
                }
            }

        })
    });

}




